package com.csi.string;
class Abc{
private int employeeId;
private String employeeName;
private String employeeAddress;
public void Cbc(int empId, String empName, String empAddress) {
	
	this.employeeId = empId;
	this.employeeName = empName;
	this.employeeAddress = empAddress;
	System.out.println("ID :"+employeeId);
	System.out.println("NAME :"+employeeName);
	System.out.println("ADDRESS :"+employeeAddress);
}


 

}
public class AbstractionConcept {
public static void main(String[] args) {
	Abc bb= new Abc();
	bb.Cbc(21, "PRADIP", "PUNE ");
}
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    